@import 'tailwindcss/base';
@import 'tailwindcss/components';
@import 'tailwindcss/utilities';

/* ドロップダウンメニュー */
.dropdwn_menu {
    zoom: 1;
    background-color: black;
    border: small solid whitesmoke;
    text-align: center;
}

.dropdwn_menu li {
    float: left;
    width: 200px;
    height: 36px;
    position: relative;
    list-style-type: none;
    border-top: 1px solid whitesmoke;
    border-right: 1px solid whitesmoke;
}

.dropdwn_menu:first-child {
    border-left: 1px solid whitesmoke;
}

.dropdwn_menu li:hover {
    background-color: rgb(77, 77, 77);
}

* html .dropdwn_menu li {
    display: inline;
    zoom: 1;
}

*+html .dropdwn_menu li {
    display: inline;
    zoom: 1;
}

.dropdwn_menu a {
    display: block;
    width: 100%;
    height: 100%;
    line-height: 36px;
    font-weight: bold;
    color: whitesmoke;
    text-decoration: none;
    position: relative;
}

.dropdwn_menu ul.sub2 {
    display: none;
    background-color: black;
}

* html .dropdwn_menu ul.sub2 {
    zoom: 1;
    position: relative;
}

*+html .dropdwn_menu ul.sub2 {
    zoom: 1;
    position: relative;
}

.dropdwn_menu ul.sub2 li {
    float: none;
}

.dropdwn_menu ul.sub2 li ul.sub2 {
    position: absolute;
    left: 200px;
    top: 0;
}

.dropdwn_menu:after {
    height: 0;
    visibility: hidden;
    content: ".";
    display: block;
    clear: both;
}

.triangle_down {
    border-top: solid 6px whitesmoke;
    border-right: solid 6px transparent;
    border-left: solid 6px transparent;
    border-bottom: solid 6px transparent;
}

.triangle_right {
    border-left: solid 6px whitesmoke;
    border-right: solid 6px transparent;
    border-top: solid 6px transparent;
    border-bottom: solid 6px transparent;
}

/* 各ページ共通 */
.pageView {
    margin-top: 3px;
    margin-left: 10px;
    font-size: 16px;
}

.container{
    width: 1300px;
    margin: 0 auto;
    padding: 20px;
    background-color: whitesmoke;
}

.title {
    margin-top: 20px;
    font-size: 30px;
    text-align: center;
}

/* ボタン */
  .btn {
    @apply py-1 px-3 mb-1 items-end rounded;
  }
  .btn-green {
    @apply bg-green-600 text-white;
  }
  .btn-blue:hover {
    @apply bg-blue-700;
  }

  /* テーブル */
/table{
  width: 100%;
  border-collapse: collapse;
  text-align: center;
}
th{
  border: 1px solid black;
}
td{
  width: 70px;
  border: 1px solid black;
}
thead th{
  height: 35px;
  background-color: gray;
  color: white;
}
tbody td{
  height: 45px;
}
tbody tr:nth-child(2n){
  background-color: lightgray;
}
